$(function(){
  $("tr").click(function(){
    $("tr .active").removeClass("active");
    var td = $(this).children();
    td.each(function(){
      $(this).addClass("active");
    });
    return false;
  });
  $("#sidebar").click(function(){
    $(this).addClass("sidebar-w");
    $("#content").addClass("content-w");
  });
});