$(function(){
  $("tr").click(function(){
    $("tr .active").removeClass("active");
    var td = $(this).children();
    td.each(function(){
      $(this).addClass("active");
    });
    return false;
  });
  $("#sidebar").click(function(){
    if($(this).hasClass("sidebar-w")){
      $(this).removeClass("sidebar-w").addClass("sidebar").html("&gt;");
      $("#content").removeClass("content-w").addClass("content");
    } else {
      $(this).removeClass("sidebar").addClass("sidebar-w").html("&lt;");
      $("#content").removeClass("content").addClass("content-w");
    }
    return false;
  });
});